import React, { useEffect, useState } from "react";
import io from "socket.io-client";

const API = "http://localhost:5000";

const socket = io(API);

function App() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState("");
  const [newUser, setNewUser] = useState("");
  const [leaderboard, setLeaderboard] = useState([]);
  const [lastClaim, setLastClaim] = useState(null);
  const [history, setHistory] = useState([]);

  // Fetch users and leaderboard
  const fetchUsers = async () => {
    const res = await fetch(`${API}/api/users`);
    setUsers(await res.json());
  };
  const fetchLeaderboard = async () => {
    const res = await fetch(`${API}/api/leaderboard`);
    setLeaderboard(await res.json());
  };
  const fetchHistory = async () => {
    const res = await fetch(`${API}/api/history`);
    setHistory(await res.json());
  };

  useEffect(() => {
    fetchUsers();
    fetchLeaderboard();
    fetchHistory();
    socket.on("leaderboardUpdate", () => {
      fetchUsers();
      fetchLeaderboard();
      fetchHistory();
    });
    return () => socket.off("leaderboardUpdate");
    // eslint-disable-next-line
  }, []);

  // Add new user
  const handleAddUser = async () => {
    if (!newUser.trim()) return;
    await fetch(`${API}/api/users`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: newUser }),
    });
    setNewUser("");
  };

  // Claim points
  const handleClaim = async () => {
    if (!selectedUser) return;
    const res = await fetch(`${API}/api/claim`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ userId: selectedUser }),
    });
    const data = await res.json();
    setLastClaim({
      ...data,
      name: users.find((u) => u._id === selectedUser)?.name,
    });
  };

  return (
    <div style={{ maxWidth: 700, margin: "40px auto", fontFamily: "Arial" }}>
      <h1>Leaderboard System</h1>
      <div style={{ marginBottom: 20 }}>
        <select
          value={selectedUser}
          onChange={(e) => setSelectedUser(e.target.value)}
        >
          <option value="">Select User</option>
          {users.map((u) => (
            <option key={u._id} value={u._id}>
              {u.name}
            </option>
          ))}
        </select>
        <button onClick={handleClaim} style={{ marginLeft: 10 }}>
          Claim
        </button>
        <input
          type="text"
          placeholder="Add new user"
          value={newUser}
          onChange={(e) => setNewUser(e.target.value)}
          style={{ marginLeft: 20 }}
        />
        <button onClick={handleAddUser} style={{ marginLeft: 5 }}>
          Add User
        </button>
      </div>
      {lastClaim && (
        <div style={{ marginBottom: 20, color: "green" }}>
          {lastClaim.name} awarded <b>{lastClaim.pointsAwarded}</b> points!
        </div>
      )}
      <h2>Leaderboard</h2>
      <table
        border="1"
        cellPadding="8"
        style={{ width: "100%", marginBottom: 30 }}
      >
        <thead>
          <tr>
            <th>Rank</th>
            <th>Name</th>
            <th>Total Points</th>
          </tr>
        </thead>
        <tbody>
          {leaderboard.map((user) => (
            <tr key={user._id}>
              <td>{user.rank}</td>
              <td>{user.name}</td>
              <td>{user.totalPoints}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h2>Claim History</h2>
      <table border="1" cellPadding="6" style={{ width: "100%" }}>
        <thead>
          <tr>
            <th>User</th>
            <th>Points</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          {history.map((h) => (
            <tr key={h._id}>
              <td>{h.userId?.name || "Unknown"}</td>
              <td>{h.points}</td>
              <td>{new Date(h.claimedAt).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
