import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import "./index.css";

const API = "http://localhost:5000";
const socket = io(API);

function formatPoints(points) {
  return (
    points?.toLocaleString(undefined, {
      minimumFractionDigits: 3,
      maximumFractionDigits: 3,
    }) || "0.000"
  );
}

const defaultAvatar = "images/pfp";

const App = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState("");
  const [newUser, setNewUser] = useState("");
  const [newUserImage, setNewUserImage] = useState("");
  const [leaderboard, setLeaderboard] = useState([]);
  const [lastClaim, setLastClaim] = useState(null);
  const [history, setHistory] = useState([]);

  // Fetch users and leaderboard
  const fetchUsers = async () => {
    const res = await fetch(`${API}/api/users`);
    setUsers(await res.json());
  };
  const fetchLeaderboard = async () => {
    const res = await fetch(`${API}/api/leaderboard`);
    setLeaderboard(await res.json());
  };
  const fetchHistory = async () => {
    const res = await fetch(`${API}/api/history`);
    setHistory(await res.json());
  };

  useEffect(() => {
    fetchUsers();
    fetchLeaderboard();
    fetchHistory();
    socket.on("leaderboardUpdate", () => {
      fetchUsers();
      fetchLeaderboard();
      fetchHistory();
    });
    return () => socket.off("leaderboardUpdate");
    // eslint-disable-next-line
  }, []);

  // Add new user
  const handleAddUser = async () => {
    if (!newUser.trim()) return;
    await fetch(`${API}/api/users`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: newUser, profileImage: newUserImage }),
    });
    setNewUser("");
    setNewUserImage("");
  };

  // Claim points
  const handleClaim = async () => {
    if (!selectedUser) return;
    const res = await fetch(`${API}/api/claim`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ userId: selectedUser }),
    });
    const data = await res.json();
    setLastClaim({
      ...data,
      name: users.find((u) => u._id === selectedUser)?.name,
    });
  };

  // Split leaderboard
  const top3 = leaderboard.slice(0, 3);
  const lowerRanks = leaderboard.slice(3, 9); // up to 9th place

  // Helper to get user image
  const getUserImage = (user) => user?.profileImage || defaultAvatar;

  return (
    <div className="min-h-screen bg-gradient-to-b from-orange-200 to-yellow-200 font-sans relative">
      {/* Top Navigation Bar */}
      <div className="relative flex items-center justify-between p-4 text-white z-10">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M10 19l-7-7m0 0l7-7m-7 7h18"
          />
        </svg>
        <div className="flex items-center space-x-2">
          <span className="text-sm">Settlement time 2 days 01:45:29</span>
        </div>
        <div className="bg-gradient-to-r from-yellow-400 to-orange-500 text-white text-sm px-4 py-2 rounded-full flex items-center space-x-1 shadow-md">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-4 w-4"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"
            />
          </svg>
          <span>Rewards</span>
        </div>
      </div>

      {/* Gold Trophy */}
      <div className="relative flex justify-center mt-4 mb-8 z-10">
        <img
          src="https://placehold.co/120x120/FCD34D/FFFFFF?text=Trophy"
          alt="Gold Trophy"
          className="w-28 h-28 object-contain"
        />
      </div>

      {/* Top 3 Rankings */}
      <div className="relative flex justify-around items-end space-x-2 mx-4 bg-white rounded-2xl shadow-lg p-4 pt-16 -mt-16 z-10">
        {/* Second Place */}
        <div className="flex flex-col items-center -mb-8">
          {top3[1] ? (
            <>
              <div className="relative">
                <img
                  src={getUserImage(top3[1])}
                  alt="User"
                  className="w-20 h-20 rounded-full border-4 border-gray-300 object-cover"
                />
                <div className="absolute -top-2 left-1/2 transform -translate-x-1/2 bg-yellow-400 text-white text-xs px-2 py-1 rounded-full font-bold">
                  2
                </div>
              </div>
              <p className="mt-2 text-gray-700 font-semibold text-sm text-center">
                {top3[1].name}
              </p>
              <div className="bg-yellow-100 text-yellow-800 text-xs px-3 py-1 rounded-full mt-1 flex items-center space-x-1">
                <span>{formatPoints(top3[1].totalPoints)}</span>
              </div>
            </>
          ) : null}
        </div>
        {/* First Place */}
        <div className="flex flex-col items-center z-10">
          {top3[0] ? (
            <>
              <div className="relative">
                <img
                  src={getUserImage(top3[0])}
                  alt="User"
                  className="w-24 h-24 rounded-full border-4 border-yellow-400 object-cover"
                />
                <div className="absolute -top-2 left-1/2 transform -translate-x-1/2 bg-yellow-500 text-white text-sm px-3 py-1 rounded-full font-bold">
                  1
                </div>
                <img
                  src="https://placehold.co/30x30/FFD700/FFFFFF?text=👑"
                  alt="Crown"
                  className="absolute top-0 right-0 w-8 h-8"
                />
              </div>
              <p className="mt-2 text-gray-800 font-bold text-base text-center">
                {top3[0].name}
              </p>
              <div className="bg-yellow-100 text-yellow-800 text-xs px-3 py-1 rounded-full mt-1 flex items-center space-x-1">
                <span>{formatPoints(top3[0].totalPoints)}</span>
              </div>
            </>
          ) : null}
        </div>
        {/* Third Place */}
        <div className="flex flex-col items-center -mb-8">
          {top3[2] ? (
            <>
              <div className="relative">
                <img
                  src={getUserImage(top3[2])}
                  alt="User"
                  className="w-20 h-20 rounded-full border-4 border-gray-300 object-cover"
                />
                <div className="absolute -top-2 left-1/2 transform -translate-x-1/2 bg-yellow-600 text-white text-xs px-2 py-1 rounded-full font-bold">
                  3
                </div>
              </div>
              <p className="mt-2 text-gray-700 font-semibold text-sm text-center">
                {top3[2].name}
              </p>
              <div className="bg-yellow-100 text-yellow-800 text-xs px-3 py-1 rounded-full mt-1 flex items-center space-x-1">
                <span>{formatPoints(top3[2].totalPoints)}</span>
              </div>
            </>
          ) : null}
        </div>
      </div>

      {/* Lower Ranking List - single color background */}
      <div className="mx-4 mt-4 rounded-2xl shadow-lg p-4 pb-0 z-10 bg-yellow-50">
        {lowerRanks.map((item, index) => (
          <div
            key={item._id}
            className="flex items-center justify-between py-3 border-b border-gray-100 last:border-b-0"
          >
            <div className="flex items-center space-x-4">
              <span className="text-gray-600 font-semibold w-8 text-center">
                {item.rank}
              </span>
              <img
                src={getUserImage(item)}
                alt="User"
                className="w-10 h-10 rounded-full object-cover"
              />
              <span className="text-gray-800 font-medium">{item.name}</span>
            </div>
            <div className="flex items-center space-x-1">
              <span className="text-yellow-800 font-semibold">
                {formatPoints(item.totalPoints)}
              </span>
              <img
                src="https://placehold.co/20x20/FCD34D/FFFFFF?text=Coin"
                alt="Coin"
                className="w-5 h-5 object-contain"
              />
            </div>
          </div>
        ))}
      </div>

      {/* User selection, claim, and add user */}
      <div className="mx-4 mt-8 bg-white rounded-2xl shadow-lg p-4 z-10 flex flex-col md:flex-row items-center justify-between space-y-4 md:space-y-0 md:space-x-4">
        <select
          value={selectedUser}
          onChange={(e) => setSelectedUser(e.target.value)}
          className="border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-orange-300"
        >
          <option value="">Select User</option>
          {users.map((u) => (
            <option key={u._id} value={u._id}>
              {u.name}
            </option>
          ))}
        </select>
        <button
          onClick={handleClaim}
          className="bg-gradient-to-r from-yellow-400 to-orange-500 text-white px-6 py-2 rounded-full shadow-md hover:from-yellow-500 hover:to-orange-600 transition"
        >
          Claim
        </button>
        <input
          type="text"
          placeholder="Add new user"
          value={newUser}
          onChange={(e) => setNewUser(e.target.value)}
          className="border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-orange-300"
        />
        <input
          type="text"
          placeholder="Profile image URL (optional)"
          value={newUserImage}
          onChange={(e) => setNewUserImage(e.target.value)}
          className="border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-orange-300"
        />
        <button
          onClick={handleAddUser}
          className="bg-gradient-to-r from-yellow-400 to-orange-500 text-white px-6 py-2 rounded-full shadow-md hover:from-yellow-500 hover:to-orange-600 transition"
        >
          Add User
        </button>
      </div>

      {/* Last claim notification */}
      {lastClaim && (
        <div className="mx-4 mt-4 bg-green-100 text-green-800 rounded-2xl shadow-lg p-4 z-10 text-center">
          {lastClaim.name} awarded <b>{lastClaim.pointsAwarded}</b> points!
        </div>
      )}

      {/* Claim History */}
      <div className="mx-4 mt-8 mb-8 bg-white rounded-2xl shadow-lg p-4 z-10">
        <h2 className="text-lg font-bold mb-4">Claim History</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full text-sm">
            <thead>
              <tr>
                <th className="px-2 py-1 text-left">User</th>
                <th className="px-2 py-1 text-left">Points</th>
                <th className="px-2 py-1 text-left">Time</th>
              </tr>
            </thead>
            <tbody>
              {history.map((h) => (
                <tr key={h._id}>
                  <td className="px-2 py-1">{h.userId?.name || "Unknown"}</td>
                  <td className="px-2 py-1">{h.points}</td>
                  <td className="px-2 py-1">
                    {new Date(h.claimedAt).toLocaleString()}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default App;
